import React, { useState } from 'react'
import { useQuery } from 'react-query';
import { Flex } from "rebass/styled-components";
import './ToDoList.css'
import { getAll, getUserId } from '../services/api'
import { useForm } from 'react-hook-form'
import Item from './Item';

const ToDoList = () => {
    const [userId, setUserId] = useState("");

    const { handleSubmit, register } = useForm({
        defaultValues: {
            userId: ''
        }
    });
    const onSubmit = handleSubmit((data) => {
        setUserId(data.userId);

    })
    const { data, error, status } = useQuery(["userItemId", userId], () =>
        getUserId(userId)
    );
    // const { data, status, error, } = useQuery("todos", getAll);
    // console.log(data)

    if (status === "loading") {
        return <p>Cargando...</p>;
    }
    if (error === "error") {
        return <p>Error</p>;
    }
    if (status === "success") {

        return (
            <div>
               <form onSubmit={onSubmit}>
                <label htmlFor='userId'>User Identification</label>
                <input ref={register({ required: true, min: 1 })} id="userId" name="userId" type="text" />
                <button>
                    Submit
                </button>
            </form>
                <Flex flexDirection="column" alignItems="start">
                    {data.map(({ userId, title, id, completed }) => (
                        <Item title={title} userId={userId} completed={completed} key={id} id={id} />
                    ))}
                </Flex>
            </div>
        )
    }
}

export default ToDoList
