import React, { useState } from 'react'
import { useMutation, useQueryClient } from "react-query";
import { useQuery } from 'react-query'
import api from '../services/api'
import './ToDoList.css'
import { addTodo } from '../services/api'
const ToDoList = () => {

    const [task, setTask] = useState('');
    const { data, status, error, } = useQuery("todos", () =>
        api(`https://jsonplaceholder.typicode.com/todos`)

    );
    console.log(data)
    const mutation = useMutation(addTodo, {
        onSuccess: () => {
            // Invalidate and refetch
            setTask("")
            queryClient.invalidateQueries("todos");
        },
    });


    if (status === "loading") {
        return <p>Cargando...</p>;
    }
    if (error === "error") {
        return <p>Error</p>;
    }
    if (status === "success") {

        return (
            <div className="container mt-5">
                <h1 className="text-center">TO DO LIST</h1>
                <hr />
                <div className="row">

                    <div className="col-8">
                        <h4 className="text-center">Lista de Tareas</h4>
                        <ul className="list-group">
                            {data.map((item) =>
                                <li className="list-group-item">
                                    <span className="lead" key={item.id}>{item.title}</span>
                                    {/* <span className="lead" key={item.id}>{item.userId}</span>
                                <span className="lead" key={item.id}>{item.completed}</span> */}
                                    <button
                                        className="btn btn-sm btn-danger float-right mx-2"
                                    >Eliminar</button>
                                    <button
                                        className="btn btn-sm btn-warning float-right"
                                    >Editar</button>
                                </li>
                            )}
                        </ul>
                    </div>

                    <div className="col-4">
                        <h4 className="text-center">
                            Agregar Tarea
                        </h4>
                        <form>
                            <input
                                type="text"
                                className="form-control mb-2"
                                placeholder="Ingrese Tarea"
                                onChange={e => setTask(e.target.value)}
                                value={task}
                            />
                            <input
                                type="number"
                                className="form-control mb-2"
                            />
                            <select name="select">
                                <option value="value1">Completed</option>
                                <option value="value2">Not completed</option>
                            </select>
                            <button className="btn btn-dark btn-block" type="submit" onClick={() => {
                                mutation.mutate(task);
                            }}>Agregar</button>
                        </form>
                    </div>

                </div>
            </div>
        )
    }
}

export default ToDoList
