import React from 'react'
import { useLocation } from "react-router";
import { useQuery } from 'react-query';
import { Flex } from "rebass/styled-components";
import './ToDoList.css'
import { getAll } from '../services/api'
import Item from './Item';
import SearchBar from '../components/SearchBar';
import { useInfiniteQuery } from 'react-query';

const ToDoList = ({search}) => {

    // const { data, status, error, } = useQuery("todos", getAll);
    // console.log(data)
    const { data, status, error, } = useInfiniteQuery(['todos', search], () => {
        const searchUrl = search ? '/search/todos?query=' + search : '/'
       return getAll(searchUrl)
    })
    // function useQueryParams() {
    //     return new URLSearchParams(useLocation().search);
    // }
    // const query = useQueryParams();
    // const search = query.get('search');
    // console.log(search)
    // const searchUrl = search ? '/search/todos?query=' + search : '/todos'

    if (status === "loading") {
        return <p>Cargando...</p>;
    }
    if (error === "error") {
        return <p>Error</p>;
    }
    if (status === "success") {

        return (
            <div>
                <SearchBar/>
                <Flex flexDirection="column" alignItems="start">
                    {data.map(({ userId, title, id, completed }) => (
                        <Item title={title} userId={userId} completed={completed} key={id} id={id} />
                    ))}
                </Flex>
            </div>
        )
    }
}

export default ToDoList
