import React from 'react'
import { useMutation, useQueryClient } from "react-query";
import { Flex, Text } from "rebass/styled-components";
import { Link} from 'react-router-dom'
import { removeItem } from '../services/api';

const Item = ({title, userId, completed, id}) => {

  const queryClient = useQueryClient();
  const {mutateAsync, isLoading} = useMutation(removeItem);

  const remove = async () => {
    await mutateAsync(id)
    queryClient.invalidateQueries("todos")
  }

  return (
    <Flex>
        <Link to={`/update/${id}`}>{title}</Link>
        <Text>{userId}</Text>
        <Text>{completed}</Text>
        <button onClick={remove}> Remove 
          {isLoading ? <span>cargando</span> : 'Remove'}
        </button>
    </Flex>
  )
}

export default Item
